declare class Sprite {
    constructor(x?: number, y?: number, 
                w?: number, h?: number, 
                collider?: "dynamic" | "static" | "kinematic" | "none" | "d" | "s" | "k" | "n");
    collider: "dynamic" | "static" | "kinematic" | "none" | "d" | "s" | "k" | "n";
    allowSleeping: boolean;
    halfHeight: number;
    halfWidth: number;
    height: number;
    mass: number;
    rotation: number;
    velocity: p5.Vector;
    width: number;
}

declare class Group {
    constructor();

    Sprite: new (
        x?: number, y?: number,
        w?: number, h?: number,
        collider?: "dynamic" | "static" | "kinematic" | "none" | "d" | "s" | "k" | "n"
    ) => Sprite;
}

declare class GlueJoint {
    constructor(spriteA: Sprite, spriteB: Sprite);
}




declare function clear(): void;

declare class World {
    constructor(timestep?: number, velocityIterations: number = 8, positionIterations: number = 3);
    
    allowSleeping: boolean;
    gravity: {
        x: number,
        y: number
    }
    velocityThreshold: number;

    getSpriteAt(x: number, y: number, group?: Group): Sprite;
}

let world: World;